"use strict";$(function(){home.init()});var home={init:function(){this.queryFoodsById(),this.bindEvent()},queryFoodsById:function(){var i=$("#dish").val();$.ajax({url:"/api/queryFoodListById",data:{id:i},success:function(i){if(0==+(i=JSON.parse(i)).result){var t=i.result_rows.map(function(i){return'<li class="bdr-bottom js-check-food" data-id='+i.id+"><i></i><span>"+i.name+"</span></li>"}).join("");$("#food_list").html(t)}else alert("获取食品列表出错")}})},submitFood:function(){var i=$("#date").val(),t=$("#food_list").find("li.active").data("id");if(0===$("#date").find("option").length)return utils.showToast("当前时段不可点餐，请周一再试"),!1;if(!t)return utils.showToast("请选择食品品类"),!1;var s={date:i,food:t};$.ajax({url:"/api/submitOrderReverse",timeout:5e3,data:s,success:function(i){0==+(i=JSON.parse(i)).result?utils.showToast("提交预约成功"):utils.showToast("提交预约失败")},error:function(i,t){utils.showToast(t)}})},bindEvent:function(){$("#dish").on("change",this.queryFoodsById),$("body").on("click",".js-check-food",function(){$(this).addClass("active").siblings().removeClass("active")}),$("body").on("click",".js-submit-food",this.submitFood)}};